  static bool
  create_schema (database& db, unsigned short pass, bool drop)
  {
    ODB_POTENTIALLY_UNUSED (db);
    ODB_POTENTIALLY_UNUSED (pass);
    ODB_POTENTIALLY_UNUSED (drop);

    if (drop)
    {
      switch (pass)
      {
        case 1:
        {
          return true;
        }
        case 2:
        {
          db.execute ("DROP TABLE IF EXISTS \"NORTHWIND\".\"EMPLOYEES\" CASCADE");
          return false;
        }
      }
    }
    else
    {
      switch (pass)
      {
        case 1:
        {
          db.execute ("CREATE TABLE \"NORTHWIND\".\"EMPLOYEES\" (\n"
                      "  \"EMPLOYEE_ID\" INTEGER NOT NULL PRIMARY KEY,\n"
                      "  \"LASTNAME\" TEXT NOT NULL,\n"
                      "  \"FIRSTNAME\" TEXT NOT NULL,\n"
                      "  \"TITLE\" TEXT NULL,\n"
                      "  \"TITLE_OF_COURTESY\" TEXT NULL,\n"
                      "  \"BIRTHDATE\" DATE NULL,\n"
                      "  \"HIREDATE\" DATE NULL,\n"
                      "  \"ADDRESS\" TEXT NULL,\n"
                      "  \"CITY\" TEXT NULL,\n"
                      "  \"REGION\" TEXT NULL,\n"
                      "  \"POSTAL_CODE\" TEXT NULL,\n"
                      "  \"COUNTRY\" TEXT NULL,\n"
                      "  \"HOME_PHONE\" TEXT NULL,\n"
                      "  \"EXTENSION\" TEXT NULL,\n"
                      "  \"PHOTO\" TEXT NULL,\n"
                      "  \"NOTES\" TEXT NULL,\n"
                      "  \"REPORTS_TO\" INTEGER NULL)");
          return false;
        }
      }
    }

    return false;
  }

